import "./card.css"
import Contador from "../contador/Contador.jsx"
import { Link } from "react-router-dom";
import {addFavorites, deleteFavorites} from "../../redux/actions"
import { connect } from "react-redux";
import { useState, useEffect } from "react";

function Card({props, id, myFavorites, deleteFavorites, addFavorites, onClose}) {   
  const [isFav, setFav] = useState(false);

  useEffect(() => {
    myFavorites.forEach((fav) => {
      if(fav.id === props.id){
        setFav(true);
      }
    });
  }, [myFavorites]);

  function handleFavorite(){
    if(isFav === true){
       setFav(false);
      deleteFavorites(id); 
    }else{
      setFav(true);
      addFavorites(props);
    }
  }

   return (
      <div className="card_container">
        <div className="card2_container"> 
         <div className="contador_container">
          <Contador className="contador" numero={0}/>
         {
          isFav ? (
           <button onClick={handleFavorite}>‚ù§Ô∏è</button>
          ) : (
           <button onClick={handleFavorite}>ü§ç</button>
          )
         }
         <button className="butonClose" onClick={props.onClose()}>X</button>
         </div>
         <Link to={`/DetailCard/${props.id}`}> 
           <h2 className="H2_1">{props.name}</h2>
         </Link>
         <img class="img_card" src={props.image} alt={props.name} />
         <h2 className="H2_2">{props.species}</h2>
         <h2 className="H2_3">{props.gender}</h2>
      </div>
     </div> 
   );
}

export function mapDispatchToProps(dispatch){
  return {
    addFavorites: (prop) => {
      dispatch(addFavorites(prop))
    },
    deleteFavorites: (id) => {
      dispatch(deleteFavorites(id))
    }
  }
}

export function mapStateToProps(state){
 return {
  myFavorites: state.myFavorites
 }
}

export default connect(mapStateToProps,mapDispatchToProps)(Card);
