import { ADD_FAVORITES, DELETE_FAVORITES, FILTER, ORDER } from "./type";

const initialState = {
   myFavorites: [],
   allCharacters: [] 
}

function rootReducer(state= initialState, actions){
  switch (actions.type) {
    case ADD_FAVORITES:
      return {
        ...state,
        myFavorites: [...state.myFavorites, actions.payload],
        allCharacters: [...state.allCharacters, actions.payload] 
      }
    case DELETE_FAVORITES:
      return {
        ...state,
       myFavorites: state.myFavorites.filter((e) => e.id !== actions.payload )  
      }
    case FILTER:
      return{
       ...state,
       allCharacters : state.allCharacters.filter((e) => e.status === actions.payload)
      } 
    case ORDER: 
      return{
        ...state,
        allCharacter: state.allCharacters.sort(function(a, b){
          if(a.id > b.id && actions.payload === "Ascendiente"){
              return 1;
          }
           if(a.id < b.id && actions.payload === "Descendiente"){
            return -1;
          }
          return 0;
        }) 
      }  
      
    default:
        return {
          ...state
        }
  }
}

export default  rootReducer;
