import { ADD_FAVORITES, DELETE_FAVORITES } from "./type";

const initialState = {
   myFavorites: [] 
}

function reducer(state= initialState, actions){
  switch (actions.type) {
    case "ADD_FAVORITES":
      return {
        ...state,
        myFavorites: [actions.payload] 
      }
    case "DELETE_FAVORITES":
      return {
       ...state,
       myFavorites: state.myFavorites.filter((e) => actions.payload !== e.id)  
      }
      
    default:
        return {
          ...state
        }
  }
}

export default rootReducer;
